import React from 'react'
import { Formik, Form, Field, ErrorMessage} from "formik"
import * as Yup from "yup"


function CreatePost() {
    const initialValues = {
        title: "",
        content: "",
        username:"",
        tags:"",
    };

    const validationSchema = Yup.object().shape({
        title: Yup.string().min(5).max(100).required(),
        content: Yup.string().min(100).required(),
        username: Yup.string().required(),
        tags: Yup.string().min(1).max(10)
    });

    const onSubmit = (data) => {
        console.log(data);
    };
    return (
        <div className="createPostPage">
        
        <Formik initialValues={initialValues} onSubmit={onSubmit}  validationSchema={validationSchema}>
            <Form className="formContainer">
                <label>Titulo: </label>
                <ErrorMessage name="title" component="span" />
                <Field id="inputCreatePost" name="title" placeholder="Escreva o título do post " />

                <label>Conteúdo: </label>
                <Field id="inputCreatePost" name="content" placeholder="Escreva o título do post " />

                <label>username: </label>
                <Field id="inputCreatePost" name="username" placeholder="Escreva o título do post " />


                <label>Tags: </label>
                <Field id="inputCreatePost" name="tags" placeholder="Escreva o título do post " />

                <button type="submit"> Criar o Post </button>

            </Form>     
             
        </Formik>   
        </div>
    )
}

export default CreatePost;
